@implementation NSKeyedArchiverTests

- (void)setUp {
    [super setUp];
}

- (void)tearDown {
    [super tearDown];
}

- (void)test01 {
  dispatch_queue_t queue = dispatch_queue_create("com.aqnote.queue.serial.tmp", DISPATCH_QUEUE_SERIAL);  // 8 threads
  
  NSArray *paths = NSSearchPathForDirectoriesInDomains(NSDocumentDirectory, NSUserDomainMask, YES);
  NSString *documentsDirectory = [paths objectAtIndex:0];
  NSString *dir = [NSString stringWithFormat:@"%@/%@", documentsDirectory, @"yudao.archive"];
  
  NSCondition *condition = [[NSCondition alloc] init];
  NSString *content = @"";
  dispatch_async(queue, ^{
    int i= 0;
    while(i++ >= 0) {
      NSString *_dir = [NSString stringWithFormat:@"%@%d", dir, i];
      [NSKeyedArchiver archiveRootObject:content toFile:_dir];
//      NSNumber *content = [NSKeyedUnarchiver unarchiveObjectWithFile:_dir];
//      sleep(1);
//      NSLog(@"%d %@", i, content);
    }
    [condition lock];
    [condition signal];
    [condition unlock];
  });
  
  [condition lock];
  [condition wait];
  [condition unlock];
  NSLog(@"Done");
}

@end
